import {createStore} from "vuex";


export default createStore({
    state: {
        needAuth : true,
        token: "",
    },

    mutations: {
        login(state){
            state.needAuth = false;
        },
        logout(state){
            state.needAuth = true;
        },
        setToken(state, token){
            state.token = token;
        }
    },

    getters: {
        isAuth(state){
            return state.isAuthenticated;
        }
    },

    actions: {

    },


    modules: {

    }
})
