import {createStore} from "vuex";
import { authModule } from "@/Store/authModule";

export default createStore({
    state: {
        
    },

    mutations: {
        login(state){
            state.needAuth = false;
        },
        logout(state){
            state.needAuth = true;
        },
        setToken(state, token){
            state.token = token;
        }
    },

    getters: {
    },

    actions: {

    },

    modules: {
        auth: authModule
    }
})
