export const noteModule  = {
    state: {
        notes: [],
    },
    getters: {
    },
    mutations:{

    },

    actions:{
        async getNotes(context) {
            let resposne = await fetch("https://localhost:7232/notes", {
                method: "Get",
                headers: { "Accept": "application/json", "Authorization":`Bearer ${this.$store.state.auth.token}` },
            });
            if (resposne.ok) {
                context.state.notes = await resposne.json();
            }
        }
    },
    namespaced: true,

}
